from typing import List, Optional, Literal
from pydantic import BaseModel, Field, validator, HttpUrl
import re

class FetchOptions(BaseModel):
    proxies: List[str] = Field(default_factory=list, description="List of proxy URLs to use for fetching")
    wait_min: int = Field(default=1, ge=0, le=30, description="Minimum wait time in seconds between requests")
    wait_max: int = Field(default=3, ge=0, le=60, description="Maximum wait time in seconds between requests")
    concurrency_limit: int = Field(default=5, ge=1, le=20, description="Maximum number of concurrent browser instances")

    @validator('proxies')
    def validate_proxies(cls, v):
        for proxy in v:
            # Check if proxy has valid format
            if not re.match(r'^(http|https|socks4|socks5)://[\w.-]+(:\d+)?$', proxy):
                raise ValueError(f'Invalid proxy format: {proxy}')
        return v

    @validator('wait_max')
    def validate_wait_max(cls, v, values):
        if 'wait_min' in values and v < values['wait_min']:
            raise ValueError('wait_max must be greater than or equal to wait_min')
        return v

class FetchRequest(BaseModel):
    links: List[str] = Field(..., min_items=1, max_items=1000, description="List of URLs to fetch")
    options: FetchOptions = Field(default_factory=FetchOptions, description="Options for the fetching process")

    @validator('links')
    def validate_links(cls, v):
        valid_links = []
        for link in v:
            try:
                # Validate URL format
                if not link.startswith(('http://', 'https://')):
                    link = 'https://' + link

                # Use Pydantic's HttpUrl for validation
                HttpUrl(link)

                # Additional checks
                if len(link) > 2000:  # RFC 7230 recommends 8000, but most browsers use 2000
                    raise ValueError(f'URL too long: {link[:50]}...')

                valid_links.append(link)
            except Exception as e:
                raise ValueError(f'Invalid URL {link}: {str(e)}')

        return valid_links
